export default (headers) => {
	const lastRecordHeader = headers.get('X-Last-Record');
	const isLastRecord = lastRecordHeader != null ? (lastRecordHeader === 'isLastRecord') || (lastRecordHeader === 'true') : null;
	const meta = JSON.parse(headers.get('X-Meta') || null);
	const pagination = JSON.parse(headers.get('X-Pagination'));
	return {
		isLastRecord,
		meta,
		pagination
	};
};
