import React from 'react';
import PropTypes from 'prop-types';

const Pane = props => (
	<div className="pane" style={{ width: props.width }}>{props.children}</div>
);

Pane.propTypes = {
	width: PropTypes.string,
	children: PropTypes.node.isRequired
};

Pane.defaultProps = {
	width: '50%'
};

export default Pane;
