import React from 'react';
import { t as gettext } from 'c-3po';
import TextBox from './TextBox';
import isAlphaNumeric from '../validations/AlphaNumeric';


/**
 * The React FieldAlphanumeric component.
 *
 *
 * @class
 * @name FieldAlphanumeric
 *
 * @return {FieldAlphanumeric} An instance of the FieldAlphanumeric component.
 */
class FieldAlphanumeric extends React.Component {

	/**
	 * @private
	 * @memberOf FieldAlphanumeric
	 *
	 * @description
	 *
	 * Check if the value is alphanumeric, returns an error message otherwise.
	 *
	 * @param {String} value - input value
	 *
	 * @return {String|Boolean}
	 */
	validate(value) {
		return isAlphaNumeric(value)
			? true
			: gettext`Fältet innehåller ej tillåtna tecken. Följande tecken är tillåtna: A-Ö, a-ö, 0-9 _/+-`;
	}

	render() {
		return (
			<TextBox
				{...this.props}
				validate={this.validate}
			/>
		);
	}

}


export default FieldAlphanumeric;
