import React from 'react';
import { t as gettext } from 'c-3po';
import TextBox from './TextBox';
import isValidCustomerNumber from '../validations/CustomerNumber';

class FieldCustomerNumber extends React.Component {

	/**
	 * @private
	 * @memberOf FieldCustomerNumber
	 *
	 * @description
	 *
	 * Check if the value is a valid customer number
	 *
	 * @param {String} value
	 *
	 * @return {String|Boolean}
	 */
	validate(value) {
		return isValidCustomerNumber(value)
			? true
			: gettext`Kundnummer innehåller ogiltiga tecken. Följande tecken är tillåtna: A-Ö, a-ö, 0-9 _/+-`;
	}

	render() {
		return (
			<TextBox
				{...this.props}
				validate={this.validate.bind(this)}
			/>
		);
	}
}

export default FieldCustomerNumber;
