import React from 'react';
import PropTypes from 'prop-types';
import SelectBox from './SelectBox';

/**
 * The React FormFieldSelect component.
 *
 *
 * @class
 * @name FormFieldSelect
 *
 * @return {FormFieldSelect} An instance of the FormFieldSelect component.
 */
class FieldSelect extends React.Component {

	/**
	 * @private
	 * @memberOf FormFieldSelect
	 *
	 * @description
	 * Generate appropriate classes to display a tooltip, if any was given.
	 *
	 * @return {String}
	 */
	getClasses() {
		return ['select-container', this.props.tooltip ? 'hint hint--top' : ''].join(' ');
	}

	/**
	 * @private
	 * @memberOf FormFieldSelect
	 *
	 * @description
	 * Render the Select option list
	 *
	 * @return {Object} - React internals
	 */
	render() {
		return (
			<div className="field-container">
				<label>{this.props.title}</label>
				<div className={this.getClasses()} data-hint={this.props.tooltip}>
					<SelectBox {...this.props} />
				</div>
			</div>
		);
	}

}

FieldSelect.defaultProps = {
	tooltip: null,
	title: null
};

FieldSelect.propTypes = {
	tooltip: PropTypes.string,
	title: PropTypes.string
};

export default FieldSelect;
