import React from 'react';
import PropTypes from 'prop-types';

class PopoverContent extends React.Component {

	/**
	 * @memberOf PopoverContent
	 * @private
	 *
	 * @description
	 * Generate classnames based on current state
	 *
	 * @return {String}
	 */
	getCSSClasses() {
		return [
			'popover',
			this.props.isVisible ? 'popover-active' : ''
		].join(' ');
	}

	/**
	 * @memberOf PopoverContent
	 * @private
	 *
	 * @description
	 * Render the Popover Content and its children
	 *
	 * @return {Object}
	 */
	render() {
		return (
			<div className={this.getCSSClasses()}>{this.props.children}</div>
		);
	}

}

PopoverContent.defaultProps = {
	isVisible: false
};

PopoverContent.propTypes = {
	isVisible: PropTypes.bool,
	children: PropTypes.node.isRequired
};

export default PopoverContent;
