import AccessRights from '../accessrights/AccessRights';

const rights = new AccessRights();
const fetchRights = new Promise((resolve, reject) => {
	try {
		const accessRights = JSON.parse(window.sessionStorage.currentUserRights);
		rights.set({ rights: accessRights });
		resolve(rights);
	} catch (e) {
		reject();
	}
});

export default fetchRights;
