import React from 'react';
import PropTypes from 'prop-types';

class HeadColumn extends React.Component {

	/**
	 * @memberOf HeadColumn
	 * @private
	 *
	 * @description
	 * Generate the style
	 *
	 * @return {Object}
	 */
	getStyle() {
		return {
			width: this.props.width
		};
	}

	/**
	 * @memberOf HeadColumn
	 * @private
	 *
	 * @description
	 * Render a t header cell
	 *
	 * @return {Object}
	 */
	render() {
		return (
			<th
				style={this.getStyle()}
				className={this.props.className}
				title={this.props.title}
			>
				{this.props.children}
			</th>
		);
	}

}

HeadColumn.defaultProps = {
	width: 'auto',
	className: '',
	title: '',
	children: null
};

HeadColumn.propTypes = {
	className: PropTypes.string,
	title: PropTypes.string,
	width: PropTypes.oneOfType([
		PropTypes.string,
		PropTypes.number
	]),
	children: PropTypes.node
};

export default HeadColumn;
