/**
 * Returns the current branding configuration.
 *
 * @param {...String} namespaces Nested object path to return.
 * @return {Object} The requsted branding, or an empty object.
 *
 * @example
 * const branding = getBranding('head', 'userMenu');
 * return branding.logOutLabel || gettext`Logga ut`;
 */
function getBranding(...namespaces) {
	let config = global.window && global.window.brandingConfig;

	for (let i = 0; i < namespaces.length; i += 1) {
		if (config === undefined) {
			return {};
		}

		config = config[namespaces[i]];
	}

	return config || {};
}

export default getBranding;
