import React, { Component } from 'react';
import { t as gettext } from 'c-3po';

class ErrorBoundary extends Component {
	constructor(props) {
		super(props);
		this.state = { hasError: false };
	}

	componentDidCatch(error, info) {
		this.setState({ hasError: true });
		console.error(error, info);
	}

	render() {
		if (this.state.hasError) {
			return (
				<div className="error-boundary-container">
					<div>
						<img src="data:image/png;base64," alt="communication error" />
					</div>
					<div className="info">
						<h2>{gettext`Okänt fel!`}</h2>
						<h2>{gettext`Ett problem har uppstått.`}</h2>
						<p>{gettext`Av någon anledning kan du just nu inte arbeta som vanligt i programmet.`}</p>
						<p>{gettext`Prova att logga in på nytt och om problemet fortfarande kvarstår ber vi dig kontakta vår support.`}</p>
					</div>
				</div>
			);
		}
		return this.props.children;
	}
}

export default ErrorBoundary;
