export default {

	getSortOrder(collection) {
		return collection.getSortKey() ? {
			sortby: collection.getSortKey(),
			order: collection.getOrder()
		} : {};
	},

	getCustomQueryParams(collection) {
		return typeof collection.getCustomQueryParams === 'function' ? collection.getCustomQueryParams() : {};
	},

	getFilter(collection) {
		return collection.getFilter() ? {
			filterby: collection.getFilter()
		} : {};
	},

	getPagination(collection) {
		return collection.getPerPage() ? {
			perpage: collection.getPerPage()
		} : {};
	},

	getUrl(collection) {
		return typeof collection.url === 'function' ? collection.url() : collection.url;
	}

};
