import TestUtils from 'react-dom/test-utils';
import should from 'should';

const testDataBinding = (input, model, modelProp) => {
	should.not.exist(model.get(modelProp));
	input.value = '1';
	TestUtils.Simulate.change(input);
	TestUtils.Simulate.blur(input);
	model.get(modelProp).should.equal('1');
	model.set(modelProp, '2');
	input.value.should.equal('2');
};

export default testDataBinding;
