/**
 * @description
 *
 * Check for invalid chars
 *
 * @param {String} ccnumber - input value
 *
 * @return {Boolean}
 */
const hasInvalidChars = ccnumber => (/[^\d]/).test(ccnumber) !== false;

/**
 * @description
 *
 * Validate the length
 *
 * @param {String} ccnumber - input value
 *
 * @return {Boolean}
 */
const hasInvalidLength = ccnumber => ccnumber.length < 7 || ccnumber.length > 8;

export { hasInvalidChars, hasInvalidLength };
