/**
 * @description
 * Check if the chars are valid for an email
 *
 * @param {String} chars - input value
 *
 * @return {Boolean}
 */
const isNotValidChars = chars => (/[^\d]/).test(chars) !== false;

/**
 * @description
 * Check if the length is valid
 *
 * @param {String} value - input value
 *
 * @return {Boolean}
 */
const isNotValidLength = value => value.length < 10 || value.length > 13;

export { isNotValidChars, isNotValidLength };
