/**
 * @description
 *
 * Check for invalid chars
 *
 * @param {String} ccnumber - input value
 *
 * @return {Boolean}
 */
const hasInvalidChars = ccnumber => (/[^\d]/).test(ccnumber);

/**
 * @description
 *
 * Validate the length
 *
 * @param {String} ccnumber - iinput value
 *
 * @return {Boolean}
 */
const hasInvalidLength = ccnumber => ccnumber.length < 2 || ccnumber.length > 8;


export { hasInvalidChars, hasInvalidLength };
