import React from 'react';
import PropTypes from 'prop-types';

/**
 * The React ViewContent Component
 * The Content Container of views, wraps the
 * Content in specific wrappers used by all views.
 *
 * @class
 * @name ViewContent
 *
 * @return {ViewContent} An instance of the ViewContent component.
 */
class ViewContent extends React.Component {

	/**
	 * @memberOf ViewContent
	 * @private
	 *
	 * @description
	 * If a user sets the width to something different than the
	 * standard 95% several other css properties also needs changing,
	 * hence we have an additional css class. Minimum amount of styling
	 * should happend in components, thats why we only handle the
	 * default width here.
	 *
	 * @return {Object}
	 */
	getClasses() {
		return [
			'view-content',
			'list-view-content',
			this.props.width !== '95%' ? 'view-content-custom' : ''
		].join(' ');
	}

	/**
	 * @memberOf ViewContent
	 * @private
	 *
	 * @description
	 * Renders the view Content Container
	 *
	 * @return {Object} - React internals
	 */
	render() {
		return (
			<div className={this.getClasses()} style={{ width: this.props.width }}>
				<div className="view-inner-content">
					{this.props.children}
				</div>
			</div>
		);
	}
}


ViewContent.defaultProps = {
	width: '95%'
};

ViewContent.propTypes = {
	children: PropTypes.node.isRequired,
	width: PropTypes.oneOfType([
		PropTypes.string,
		PropTypes.number
	])
};


export default ViewContent;
