import React from 'react';
import PropTypes from 'prop-types';

/**
 * Displays a spinning animation based on
 * font awesome cogwheel.
 *
 * @class
 * @name ViewSpinner
 *
 * @return {ViewSpinner}
 */
class ViewSpinner extends React.Component {

	render() {
		if (!this.props.isActive) { return null; }
		return (
			<div className="loader" />
		);
	}

}

ViewSpinner.propTypes = {
	isActive: PropTypes.bool
};

ViewSpinner.defaultProps = {
	isActive: false
};

export default ViewSpinner;
