import React from 'react';
import PropTypes from 'prop-types';

class ViewTab extends React.Component {

	getCSSActiveClass() {
		return this.props.isActive ? 'view-tab-active ' : ' ';
	}

	render() {
		return (
			<div className={`view-tab ${this.props.className}`}>{this.props.children}</div>
		);
	}

}

ViewTab.defaultProps = {
	className: '',
	title: '',
	/**
	 * This function is not used in this class but actually
	 * picked up by the tabs component, so it is necessary.
	 * @return {null}
	 */
	onClick() { return null; }
};

ViewTab.defaultProps = {
	isActive: false
};

ViewTab.propTypes = {
	children: PropTypes.node.isRequired,
	className: PropTypes.string,
	isActive: PropTypes.bool
};

export default ViewTab;
