import React from 'react';
import PropTypes from 'prop-types';

/**
 * Renders the title of a view
 *
 * @class
 * @name ViewSpinner
 *
 * @return {ViewSpinner}
 */
const ViewTitle = props => <h2 className="title"> {props.children} </h2>;

ViewTitle.propTypes = {
	children: PropTypes.node.isRequired
};


export default ViewTitle;
