import React from 'react';
import PropTypes from 'prop-types';

/**
 * The React ViewTools Component
 * Renders the tools menu inside views
 *
 * @class
 * @name ViewTools
 *
 * @return {ViewTools} An instance of the ViewTools component.
 */
const ViewTools = props => (
	<div className="view-tools">
		<div className="ft-width ft-cmargin">
			<ul>
				{props.children}
			</ul>
		</div>
	</div>
);

ViewTools.propTypes = {
	children: PropTypes.node.isRequired
};

export default ViewTools;
