import React from 'react';
import PropTypes from 'prop-types';
import ActionMessages from '../utils/ActionMessages';


/**
 * The viewport is the entire visible area of the
 * application. It should be used in the main app/jsx
 * file wrapping all other elements.
 *
 *
 * @class Viewport
 */
const Viewport = props => (
	<section className="viewport">
		{props.children}
		<ActionMessages messages={props.actionMessageQueue} />
	</section>
);

Viewport.propTypes = {
	children: PropTypes.node.isRequired,
	actionMessageQueue: PropTypes.object.isRequired
};

export default Viewport;
