import React from 'react';
import PropTypes from 'prop-types';

/**
 * React Viewport blocker component.
 * Blocks to entire viewport behind a dialog
 *
 * @class ViewportBlocker
 */
class ViewportBlocker extends React.Component {

	/**
	 * Generate list of css classes
	 *
	 * @return {String} Default props.
	 */
	getClasses() {
		return [
			'viewport-blocker',
			this.props.isActive ? 'viewport-blocker-active' : ''
		].join(' ');
	}

	/**
	 * Render the ViewPort blocker
	 *
	 * @return {Object}
	 */
	render() {
		return <div className={this.getClasses()} />;
	}

}


ViewportBlocker.defaultProps = {
	isActive: false
};


ViewportBlocker.propTypes = {
	isActive: PropTypes.bool
};

export default ViewportBlocker;
