import React from 'react';
import ReactDOM from 'react-dom';
import TestUtils from 'react-dom/test-utils';
import Button from 'form/Button';
import 'utils/AjaxStub'
import should from 'should';

	describe('Button', () => {

		it('Should run render a clickable button', done => {

			const component = TestUtils.renderIntoDocument(
				<Button> Spara </Button>
			);

			ReactDOM.findDOMNode(component).textContent.should.equal(' Spara ');
			done();

		});


		it('Should trigger callback on click', done => {
			const fnClick = () => {
				should.ok('Calling click callback');
				done();
			};
			const component = TestUtils.renderIntoDocument(
				<Button onClick={fnClick}> Spara </Button>
			);
			TestUtils.Simulate.click(ReactDOM.findDOMNode(component));

		});

	});
