import React from 'react';
import ReactDOM from 'react-dom';
import TestUtils from 'react-dom/test-utils';
import 'utils/AjaxStub'
import Expander from 'form/Expander';

describe('Expander', () => {

	it('expands/collapses a section of the ui', done => {

		const component = TestUtils.renderIntoDocument(
			<Expander> content </Expander>
		);

		ReactDOM.findDOMNode(component).textContent.should.equal(' content ');
		component.state.isExpanded.should.be.false();
		TestUtils.Simulate.click(
			TestUtils.findRenderedDOMComponentWithTag(component, 'button')
		);
		done();
		component.state.isExpanded.should.be.true();

	});

});
