import should from 'should';
import { getFid, isValidFid } from 'utils/Fid';



describe('Fid utility', () => {

	it('extracts fids correctly from pathname', done => {
    should.not.exist(getFid('/app/invalidfid'));
    getFid('/app/059900c7c50b482a8226adc58e9c4927').should.equal('059900c7c50b482a8226adc58e9c4927');
    getFid('/app/059900c7c50b482a8226adc58e9c4927/some/view/123').should.equal('059900c7c50b482a8226adc58e9c4927');
    done();
  });

  it('validates fids correctly', done => {
    isValidFid(123).should.be.false();
    isValidFid(undefined).should.be.false();
    isValidFid('weirdString').should.be.false();
    isValidFid('058a45cc25e849fd85fd3180f94e998f').should.be.true();
    done();
  });

});
