/* global describe, it */
import 'should';
import React from 'react';
import NotificationMessage from 'utils/NotificationMessage';
import TestUtils from 'react-dom/test-utils';

describe('NotificationMessage', () => {
	it('Renders a notificationmessage', (done) => {
		const component = TestUtils.renderIntoDocument(
			<NotificationMessage>
				<span />
			</NotificationMessage>
		);
		const notificationmessage = TestUtils.findRenderedDOMComponentWithClass(component, 'notificationmessage');
		TestUtils.isDOMComponent(notificationmessage).should.equal(true);
		done();
	});

	it('Should be hidden if open is false', (done) => {
		const component = TestUtils.renderIntoDocument(
			<NotificationMessage open={false}>
				<span />
			</NotificationMessage>
		);
		const notificationmessage = TestUtils.findRenderedDOMComponentWithClass(component, 'hidden');
		TestUtils.isDOMComponent(notificationmessage).should.equal(true);
		done();
	});

	it('Renders an icon', (done) => {
		const component = TestUtils.renderIntoDocument(
			<NotificationMessage open={false}>
				<span />
			</NotificationMessage>
		);
		const notificationmessage = TestUtils.findRenderedDOMComponentWithClass(component, 'fa');
		TestUtils.isDOMComponent(notificationmessage).should.equal(true);
		done();
	});

	it('Renders a warning message', (done) => {
		const component = TestUtils.renderIntoDocument(
			<NotificationMessage type="warning">
				<span />
			</NotificationMessage>
		);
		const notificationmessage = TestUtils.findRenderedDOMComponentWithClass(component, 'warning');
		TestUtils.isDOMComponent(notificationmessage).should.equal(true);
		done();
	});
});
